import { StatusBar } from 'expo-status-bar';
import React, { useEffect }from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';

export default function App() {
  useEffect(() =>{
    // alert("window.location.href");
  }, []);

  const login = () => {
		// crie o fetch
		// passe a url, e depois como 2° param um array passando alguns dados, method, body (o data) entre outros, pesquisar caso necessario (como header e etc)
		fetch("http://192.168.1.40:3030/", {
			method: "GET",
			body: "formData"
		})
		// .then(response => response.json()) // o fetch retorna uma resposta em promise, entao para acessar o dado precisa ser por .then(), se o dado for em json use como acima
		.then(response => response.text() ) // para pegar um dado que tenha tido output do outro lado, pegue por text()
		.then(result => {
      alert(result);
			// add o resultado a div
			// document.getElementById("resultado").innerHTML = result;
		}) // depois faça o que quiser com o dado
		.catch(function(err) {
       		alert(err);
		});
  };

  return (
    <View style={styles.container}>
      <Text>va para o botao de login</Text>
      <TouchableOpacity style={styles.button} onPress={login}>
        <Text style={styles.text}>Login com API</Text>
      </TouchableOpacity>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button:{
    backgroundColor: 'blue',
    padding: 10,
    margin: 20,
    width: 200,
    color: 'white',
    borderRadius: 40,
    alignItems: 'center',
  },
  text:{
    fontWeight: 'bold',
    fontSize: 20,
  }
});
