import axios from "axios";
import UrlService from "./UrlService";
import CookieService from "./CookieService";

const expiresAt = 60 * 24;

interface Credentials {
  client_id: string;
  redirect_url: string;
  response_type: string;
  scope: string;
  state: string;
}

class AuthService {
  async doUserLogin(credentials) {
    try {
      const response = await axios.get(UrlService.conectAPI()+"?"+credentials);
      return response.data;
    } catch (error) {
      console.error("Error", error.response);
      return false;
    }
  }

  async loginUserApp(credentials) {
    try {
      const response = await axios.get(UrlService.loginClient()+"?"+credentials);
      console.log(response);
      if(response){
        let date = new Date();
        date.setTime(date.getTime() + expiresAt * 60 * 1000);
        const options = { path: "/", expires: date };
        CookieService.set("access_token", response.access_token, options);
        CookieService.set("email", response.email, options);
        return true;
      }

      // return response.data;
      return false;
    } catch (error) {
      console.error("Error", error.response);
      return false;
    }
  }

  handleLoginSuccess(response: any, remember: boolean) {
    if (!remember) {
      const options = { path: "/" };
      CookieService.set("access_token", response.access_token, options);
      return true;
    }

    let date = new Date();
    date.setTime(date.getTime() + expiresAt * 60 * 1000);
    const options = { path: "/", expires: date };
    CookieService.set("access_token", response.access_token, options);
    return true;
  }
}

export default new AuthService();
