import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View, ScrollView, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { WebView } from 'react-native-webview';

// abre uma tela no browser por webView
function PrepareToLogin() {
  // button
  const navigation = useNavigation();

  function makeid(length) {
     var result           = '';
     var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
     var charactersLength = characters.length;
     for ( var i = 0; i < length; i++ ) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
     }
     return result;
  }

  const params = new URLSearchParams({
    client_id: 3, // o id do client no nosso servidor
    redirect_url: "http://192.168.1.40:8001/callback", // a url q vai voltar
    response_type: 'code', // o tipo
    scope: '', // scope
    state: makeid(40) // a chave da transacao
  });
  // alert(params.toString());
  // alert(makeid(40));
  // "http://localhost:8000/oauth/authorize?".$query
  // const urlPrepareToLogin =
  // "http://192.168.1.40:3030/oauth/authorize?"+params.toString();
  // "http://192.168.1.40:3030/login";
  // "myapp://";

  return (
    <>
      <View style={{flex: 1, marginTop: 30}}>
        <WebView source={{ uri: urlPrepareToLogin }} style={{flex: 1}}/>
      </View>
      <TouchableOpacity style={styles.button} onPress={() => navigation.goBack()}>
        <Text style={styles.text}>voltar</Text>
      </TouchableOpacity>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    paddingTop: 30,
    paddingBottom: 20,
    backgroundColor: 'navy',
    alignItems: 'center',
  },
  button: {
    backgroundColor: 'blue',
    color: 'white',
    height: 45,
    alignItems: 'center',
    justifyContent: 'center'
  },
  text:{
    fontWeight: 'bold',
    fontSize: 20
  },
  ContainerButton:{
    justifyContent: 'flex-end',
    marginBottom: 36
  }
});

export default PrepareToLogin;
