import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View, ScrollView, TouchableOpacity, NavigationContainer } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { WebView } from 'react-native-webview';
import * as Linking from 'expo-linking';

// abre uma tela no browser por webView

// const prefix = Linking.makeURL('/');

function LinkExpo() {
  const linking = {
    prefixes: ["exp://192.168.1.49:19000"],
  };

  return (
    <NavigationContainer linking={linking} fallback={<Text>Carregando...</Text>}>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    paddingTop: 30,
    paddingBottom: 20,
    backgroundColor: 'navy',
    alignItems: 'center',
  },
  button: {
    backgroundColor: 'blue',
    color: 'white',
    height: 45,
    alignItems: 'center',
    justifyContent: 'center'
  },
  text:{
    fontWeight: 'bold',
    fontSize: 20
  },
  ContainerButton:{
    justifyContent: 'flex-end',
    marginBottom: 36
  }
});

export default LinkExpo;
