import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { useNavigation } from '@react-navigation/native';
import { StyleSheet, Text, View, TouchableOpacity, Linking } from 'react-native';
import * as Expo from 'expo';
import CookieService from "./../services/CookieService";

export default function App() {
  // button
  const navigation = useNavigation();
  const prefix = Expo.Linking.makeUrl('/');
  const [logado, setLogado] = useState(false);

  useEffect(() =>{
    // alert(prefix);
    setLogado(CookieService.get("email")? true : false);
    alert(logado);
  }, []);

  const login = () => {
		// crie o fetch
		// passe a url, e depois como 2° param um array passando alguns dados, method, body (o data) entre outros, pesquisar caso necessario (como header e etc)
		fetch("http://192.168.1.40:3030/", {
			method: "GET"
			// body: "formData"
		})
		// .then(response => response.json()) // o fetch retorna uma resposta em promise, entao para acessar o dado precisa ser por .then(), se o dado for em json use como acima
		.then(response => response.text() ) // para pegar um dado que tenha tido output do outro lado, pegue por text()
		.then(result => {
      // alert(result);
      redirect(result);

		}) // depois faça o que quiser com o dado
		.catch(function(err) {
       		alert(err);
		});
  };

  return (
    <View style={styles.container}>
      <Text>Bem vindo</Text>

      {logado == false?
        <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Login')}>
          <Text style={styles.text}>Login</Text>
        </TouchableOpacity>
      : null }

      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Inicio')}>
        <Text style={styles.text}>ir para a pagina inicio</Text>
      </TouchableOpacity>

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button:{
    backgroundColor: 'blue',
    padding: 10,
    margin: 20,
    width: 200,
    color: 'white',
    borderRadius: 40,
    alignItems: 'center',
    justifyContent: 'center',
  },
  text:{
    fontWeight: 'bold',
    fontSize: 20,
  }
});
