import React, { useEffect, useState } from 'react';
import { Alert, StyleSheet, Text, View, TouchableOpacity, Image, TextInput, ScrollView, Linking } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import axios from 'axios';
import AuthService from './../services/AuthService';

function Login() {

  // button
  const navigation = useNavigation();

  function makeid(length) {
     var result           = '';
     var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
     var charactersLength = characters.length;
     for ( var i = 0; i < length; i++ ) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
     }
     return result;
  }


  async function handleButtonPress(){
    const postData = {
      client_id: 3,
      redirect_url: 'desafio://app/home',
      response_type: 'code',
      scope: '',
      state: makeid(40),
    };

    const response = await AuthService.doUserLogin(postData);

    if (response) {
      alert("foi");
      // AuthService.handleLoginSuccess(response, this.state.isChecked);
      // this.props.history.push("/home");
    } else {
      alert("Please check your credentials and try agian");
    }
    // alerta padrao do celular
      // Alert.alert("email: " + email + " Senha: " + pass);
    // uso do axios para post
      // axios.post('http://192.168.1.40:3030/cadastrarteste', {nome},{
      // "headers": {
      //   "content-type": "application/json",
      // },
      // })
      // .then(function(response) {
      //   // console.log(response);
      // })
      // .catch(function(error) {
      //   // console.log(error);
      // });
      // `http://localhost:${config.fusionAuthPort}/oauth2/authorize?client_id=${config.clientID}&redirect_uri=${config.redirectURI}&response_type=code`
  }


  return (
    <>
      <ScrollView>
        <View style={styles.container}>
          <Text>Fa√ßa login</Text>
        </View>
        <View style={styles.inputContainer}>
          <TextInput style={styles.input} placeholder="seuemail@provedor.com" />
          <TextInput style={styles.input} placeholder="******" secureTextEntry={true} />

          <TouchableOpacity style={styles.buttonSend} onPress={handleButtonPress}>
            <Text style={styles.textButton}>Login</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button} onPress={() => navigation.goBack()}>
            <Text style={styles.textButton}>Voltar</Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    </>
  );
}

const styles = StyleSheet.create({
    inputContainer: {
        margin: 20,
        alignItems: 'stretch',
        justifyContent: 'center'
    },
    container: {
      alignItems: 'center',
      justifyContent: 'center',
    },
    topImage: {
        margin: 20,
    },
    title: {
        fontSize: 20,
    },
    input: {
        marginTop: 10,
        height: 60,
        backgroundColor: '#fff',
        borderRadius: 10,
        paddingHorizontal: 24,
        fontSize: 16,
        alignItems: 'stretch',
    },
    buttonSend: {
        marginTop: 10,
        height: 60,
        backgroundColor: 'green',
        borderRadius: 10,
        paddingHorizontal: 24,
        fontSize: 16,
        alignItems: 'center',
        justifyContent: 'center',
    },
    textButton: {
        color: '#fff',
        fontWeight: 'bold',
    },
    button:{
      marginTop: 20,
      alignItems: 'center',
      backgroundColor: '#0000ff',
      padding: 10,
    },
});

export default Login;
