import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { useNavigation } from '@react-navigation/native';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import CookieService from "./../services/CookieService";
import * as Linking from 'expo-linking';
import * as Location from 'expo-location';

export default function App() {
  // button
  const navigation = useNavigation();
  const [logado, setLogado] = useState(false);

  useEffect(() => {
    (async () => {
      setLogado(CookieService.get("email")? true : false);
    })();
  }, []);
  // ao carregar pega a localizacao, mas dentro tem um setInterval para a cada x segundos
  useEffect(() => {
    const interval = setInterval(() => {
      (async () => {
        let { status } = await Location.requestPermissionsAsync();
        if (status !== 'granted') {
          setErrorMsg('Permission to access location was denied');
          return;
        }
        if(logado)
        {
          let locationAPI = await Location.getCurrentPositionAsync({});
          setLocation(locationAPI);

          console.log("Latitude: " + locationAPI.coords.latitude + " Longitude: " + locationAPI.coords.longitude);
          // sobe para o servidor a localizacao
          // axios.post('http://192.168.1.40:8000/api/location/post',(locationAPI.coords),{
          // "headers": {
          //   "Authorization": "Bearer " + CookieService.get("access_token"),
          //   "content-type": "application/json",
          // },
          // })
          // .then(function(response) {
          //   console.log(response.data);
          // })
          // .catch(function(error) {
          //   console.log(error);
          // });
        }

      })();
      return () => clearInterval(interval);
    }, 20000);
  }, []);
  return (
    <View style={styles.container}>
      <Text>Bem vindo</Text>

      {logado == false ?
        <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Login')}>
          <Text style={styles.text}>Login</Text>
        </TouchableOpacity>
      : null }

      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Inicio')}>
        <Text style={styles.text}>ir para a pagina inicio</Text>
      </TouchableOpacity>

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button:{
    backgroundColor: 'blue',
    padding: 10,
    margin: 20,
    width: 200,
    color: 'white',
    borderRadius: 40,
    alignItems: 'center',
    justifyContent: 'center',
  },
  text:{
    fontWeight: 'bold',
    fontSize: 20,
  }
});
