import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { useNavigation, StackActions, NavigationActions } from '@react-navigation/native';
import { StyleSheet, Text, View, TouchableOpacity, Linking } from 'react-native';
import CookieService from "./../services/CookieService";

function Inicio() {

  // button
  const navigation = useNavigation();
  const nome = CookieService.get("email") ?? '';

  useEffect(() => {
    // verifica se ta logado
    if (!CookieService.get("email")) {
      alert("faça login para usar o aplicativo.");
      // limpa
      CookieService.remove("email");
      CookieService.remove("access_token");
      // limpa e volta a rota
      navigation.reset({
          index: 0,
          routes: [{ name: 'Home' }],
      });
    }
  }, []);

  function logout() {
    // limpa
    CookieService.remove("email");
    CookieService.remove("access_token");
    navigation.navigate("Home");
  }

  return (
    <View style={styles.container}>
    <Text>Parabens ! chegou aqui por link {nome}</Text>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("Home")}>
        <Text style={styles.text}>Home</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={logout}>
        <Text style={styles.text}>Sair</Text>
      </TouchableOpacity>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button:{
    backgroundColor: 'blue',
    padding: 10,
    margin: 20,
    width: 200,
    color: 'white',
    borderRadius: 40,
    alignItems: 'center',
  },
  text:{
    fontWeight: 'bold',
    fontSize: 20,
  }
});

export default Inicio;
